USE pokedex;

CREATE TABLE TYPE(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	PRIMARY KEY(ID)
);

CREATE TABLE STONE(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	PRIMARY KEY(ID)
);

CREATE TABLE POKEMON(
	POKEDEX_NO INT,
	NAME CHAR(20),
	TYPE1_ID INT,
	TYPE2_ID INT,
	WEIGHT FLOAT,
	HEIGHT FLOAT,
	PRIMARY KEY(POKEDEX_NO),
	FOREIGN KEY(TYPE1_ID) REFERENCES TYPE(ID),
	FOREIGN KEY(TYPE2_ID) REFERENCES TYPE(ID)
);

CREATE TABLE STARTER_POKEMON(
	POKEDEX_NO INT,
	PRIMARY KEY(POKEDEX_NO),
	FOREIGN KEY(POKEDEX_NO) REFERENCES POKEMON(POKEDEX_NO) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE LEGENDARY_POKEMON(
	POKEDEX_NO INT,
	PRIMARY KEY(POKEDEX_NO),
	FOREIGN KEY(POKEDEX_NO) REFERENCES POKEMON(POKEDEX_NO) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE ENGINEERED_POKEMON(
	POKEDEX_NO INT,
	PRIMARY KEY(POKEDEX_NO),
	FOREIGN KEY(POKEDEX_NO) REFERENCES POKEMON(POKEDEX_NO) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE BADGE(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	TYPE_ID INT,
	PRIMARY KEY(ID),
	FOREIGN KEY(TYPE_ID) REFERENCES TYPE(ID)	
);

CREATE TABLE TOWN(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	PRIMARY KEY(ID)
);

CREATE TABLE GYM(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	TOWN_ID INT,
	PRIMARY KEY(ID),
	FOREIGN KEY(TOWN_ID) REFERENCES TOWN(ID)
);

CREATE TABLE ROSTER(
	ID INT AUTO_INCREMENT,
	PRIMARY KEY(ID)
);

CREATE TABLE TRAINER(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	ROSTER_ID INT,
	PRIMARY KEY(ID),
	FOREIGN KEY(ROSTER_ID) REFERENCES ROSTER(ID)
);

CREATE TABLE GYM_LEADER(
	ID INT,
	GYM_ID INT NOT NULL,
	BADGE_ID INT NOT NULL,
	PRIMARY KEY(ID),
	FOREIGN KEY(ID) REFERENCES TRAINER(ID) ON DELETE CASCADE ON UPDATE CASCADE,
	FOREIGN KEY(GYM_ID) REFERENCES GYM(ID),
	FOREIGN KEY(BADGE_ID) REFERENCES BADGE(ID)
);

CREATE TABLE POKEBALL(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	CATCH_RATE FLOAT,
	PRIMARY KEY(ID)
);

CREATE TABLE CATEGORY(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	PRIMARY KEY(ID)
);

CREATE TABLE MOVE(
	ID INT AUTO_INCREMENT,
	NAME CHAR(20),
	TYPE_ID INT NOT NULL,
	CATEGORY_ID INT NOT NULL,
	PRIMARY KEY(ID),
	FOREIGN KEY(TYPE_ID) REFERENCES TYPE(ID),
	FOREIGN KEY(CATEGORY_ID) REFERENCES CATEGORY(ID)
);

CREATE TABLE TM(
	ID INT,
	MOVE_ID INT NOT NULL,
	PRIMARY KEY(ID),
	FOREIGN KEY(MOVE_ID) REFERENCES MOVE(ID)
);

CREATE TABLE NO_EFFECT_AGAINST(
	TYPE_ID_1 INT,
	TYPE_ID_2 INT,
	PRIMARY KEY(TYPE_ID_1,TYPE_ID_2),
	FOREIGN KEY(TYPE_ID_1) REFERENCES TYPE(ID),
	FOREIGN KEY(TYPE_ID_2) REFERENCES TYPE(ID)
);

CREATE TABLE NOT_VERY_EFFECTIVE_AGAINST(
	TYPE_ID_1 INT,
	TYPE_ID_2 INT,
	PRIMARY KEY(TYPE_ID_1,TYPE_ID_2),
	FOREIGN KEY(TYPE_ID_1) REFERENCES TYPE(ID),
	FOREIGN KEY(TYPE_ID_2) REFERENCES TYPE(ID)
);

CREATE TABLE SUPER_EFFECTIVE_AGAINST(
	TYPE_ID_1 INT,
	TYPE_ID_2 INT,
	PRIMARY KEY(TYPE_ID_1,TYPE_ID_2),
	FOREIGN KEY(TYPE_ID_1) REFERENCES TYPE(ID),
	FOREIGN KEY(TYPE_ID_2) REFERENCES TYPE(ID)
);

CREATE TABLE NEXT_EVO(
	POKEDEX_NO INT,
	POKEDEX_NO_NEXT INT,
	LEVEL INT,
	STONE_ID INT,
	TRADE_EVO BOOL,
	PRIMARY KEY(POKEDEX_NO,POKEDEX_NO_NEXT),
	FOREIGN KEY(POKEDEX_NO) REFERENCES POKEMON(POKEDEX_NO),
	FOREIGN KEY(POKEDEX_NO_NEXT) REFERENCES POKEMON(POKEDEX_NO),
	FOREIGN KEY(STONE_ID) REFERENCES STONE(ID)
);

CREATE TABLE MEMBER(
	ROSTER_ID INT,
	POKEDEX_NO INT,
	FIGHT_ORDER INT,
	PRIMARY KEY(ROSTER_ID, POKEDEX_NO, FIGHT_ORDER),
	FOREIGN KEY(ROSTER_ID) REFERENCES ROSTER(ID),
	FOREIGN KEY(POKEDEX_NO) REFERENCES POKEMON(POKEDEX_NO)
);



















